import { Injectable } from '@angular/core';
import { DiagramModel, PointModel } from '@ngx-diagrams/core';
import * as dagre from 'dagre';
import { EdgeConfig, GraphLabel, NodeConfig } from 'dagre';

export interface DagreEngineOptions {
  graph?: GraphLabel;
  layout?: GraphLabel & NodeConfig & EdgeConfig;
  /**
   * Will also layout links
   */
  includeLinks?: boolean;
}

@Injectable()
export class DagreEngine {
  g: dagre.graphlib.Graph;

  instantiate() {
    try {
      this.g = new dagre.graphlib.Graph({ multigraph: true });
    } catch (error) {
      console.warn("`dagre` packages isn't installed, please install it before using the DagreEngine plugin");
    }
  }

  redistribute(model: DiagramModel, options: DagreEngineOptions = {}): void {
    this.instantiate();

    this.g.setGraph(options.graph || {});

    this.g.setDefaultEdgeLabel(() => {
      return {};
    });

    const processedlinks: { [id: string]: boolean } = {};

    // set nodes
    model.getNodes().forEach((node) => {
      this.g.setNode(node.id, {
        width: node.getWidth(),
        height: node.getHeight()
      });
    });

    model.getLinks().forEach((link) => {
      // set edges
      if (link.getSourcePort() && link.getTargetPort()) {
        processedlinks[link.id] = true;
        this.g.setEdge({
          v: link.getSourcePort().getNode().id,
          w: link.getTargetPort().getNode().id,
          name: link.id
        });
      }
    });

    // layout the graph
    dagre.layout(this.g, options.layout);

    this.g.nodes().forEach((v) => {
      const { x, y } = this.g.node(v);
      model.getNode(v).setCoords({ x, y });
    });

    // also include links?
    if (options.includeLinks) {
      this.g.edges().forEach((e) => {
        const edge = this.g.edge(e);
        const link = model.getLink(e.name);

        const points = [link.getFirstPoint()];
        for (let i = 1; i < edge.points.length - 2; i++) {
          points.push(new PointModel(link, { x: edge.points[i].x, y: edge.points[i].y }));
        }
        link.setPoints(points.concat(link.getLastPoint()));
      });
    }
  }
}

import { PortModel, HashMap, LinkModel } from '@ngx-diagrams/core';
import { DefaultLinkModel } from './default-link.model';

export interface DefaultPortModelConfig {
  id?: string;
  isInput?: boolean;
  name?: string;
  type?: string;
  label?: string;
  linkType?: string;
  maximumLinks?: number;
}

export class DefaultPortModel extends PortModel {
  in: boolean;
  label: string;
  links: HashMap<DefaultLinkModel>;

  constructor({
    isInput = true,
    name = 'port',
    type = 'default',
    id = null,
    label = null,
    linkType = 'default',
    maximumLinks = null
  }: DefaultPortModelConfig = {}) {
    super(name, type, id, null, linkType);
    this.in = isInput;
    this.label = label || name;

    if (this.in) {
      this.setCanCreateLinks(false);
    } else {
      this.setMaximumLinks(maximumLinks);
      this.setMagnetic(false);
    }
  }

  link(port: PortModel): LinkModel {
    if (super.getCanCreateLinks()) {
      const link = new DefaultLinkModel({ type: this.getLinkType() });
      link.setSourcePort(this);
      link.setTargetPort(port);
      return link;
    }

    return null;
  }

  createLinkModel() {
    if (super.getCanCreateLinks()) {
      return new DefaultLinkModel({ type: this.getLinkType() });
    }
  }

  canLinkToPort(port: PortModel): boolean {
    if (port instanceof DefaultPortModel) {
      return this.in !== port.in;
    }
    return true;
  }
}

import { NgZone } from '@angular/core';

export interface ZonedClass {
  ngZone: NgZone;
}

export function OutsideZone<T extends ZonedClass>(targetClass: T, functionName: string, descriptor) {
  const source = descriptor.value;
  descriptor.value = function (...data) {
    if (!this.ngZone) {
      throw new Error("Class with 'OutsideZone' decorator should have 'ngZone' class property with 'NgZone' class.");
    }
    return this.ngZone.runOutsideAngular(() => source.call(this, ...data));
  };
  return descriptor;
}

