import { ViewContainerRef, ComponentRef } from '@angular/core';
import { LabelModel, AbstractFactory } from '@ngx-diagrams/core';

export abstract class AbstractLabelFactory<T extends LabelModel = LabelModel> extends AbstractFactory<T> {
  abstract generateWidget(label: LabelModel, labelsHost: ViewContainerRef): ComponentRef<T>;
}

import { ViewContainerRef, ComponentRef } from '@angular/core';
import { LinkModel, AbstractFactory } from '@ngx-diagrams/core';

export abstract class AbstractLinkFactory<T extends LinkModel = LinkModel> extends AbstractFactory<T> {
  abstract generateWidget(link: LinkModel, linksHost: ViewContainerRef): ComponentRef<T>;
}

import { ViewContainerRef, ComponentRef } from '@angular/core';
import { NodeModel, AbstractFactory } from '@ngx-diagrams/core';

export abstract class AbstractNodeFactory<T extends NodeModel = NodeModel> extends AbstractFactory<T> {
  abstract generateWidget(node: NodeModel, nodeHost: ViewContainerRef): ComponentRef<T>;
}

import { ViewContainerRef, ComponentRef } from '@angular/core';
import { PortModel, AbstractFactory } from '@ngx-diagrams/core';

export abstract class AbstractPortFactory<T extends PortModel = PortModel> extends AbstractFactory<T> {
  abstract generateWidget(port: PortModel, portHost: ViewContainerRef): ComponentRef<T>;
}

import {
  ViewContainerRef,
  ComponentRef,
  ComponentFactoryResolver,
  ComponentFactory,
  Renderer2,
} from '@angular/core';
import { AbstractLabelFactory, DefaultLabelModel } from 'ngx-diagrams';
import { CustomLabelComponent } from './custom-label.component';

export class CustomLabelFactory extends AbstractLabelFactory<
  DefaultLabelModel
> {
  constructor(
    private resolver: ComponentFactoryResolver,
    private renderer: Renderer2
  ) {
    super('custom-label');
  }

  generateWidget(
    label: CustomLabelComponent,
    labelHost: ViewContainerRef
  ): ComponentRef<CustomLabelComponent> {
    const componentRef = labelHost.createComponent(this.getRecipe());

    // attach coordinates and default positional behaviour to the generated component host
    const rootNode = componentRef.location.nativeElement;

    // default style for link
    this.renderer.setStyle(rootNode, 'position', 'absolute');

    // data attributes
    this.renderer.setAttribute(rootNode, 'data-labelid', label.id);

    // on destroy make sure to destroy the componentRef
    label.onEntityDestroy().subscribe(() => {
      componentRef.destroy();
    });

    // assign all passed properties to node initialization.
    Object.entries(label).forEach(([key, value]) => {
      componentRef.instance[key] = value;
    });

    return componentRef;
  }

  getRecipe(): ComponentFactory<CustomLabelComponent> {
    return this.resolver.resolveComponentFactory(CustomLabelComponent);
  }

  getNewInstance() {
    return new DefaultLabelModel();
  }
}

import {
  ViewContainerRef,
  ComponentRef,
  ComponentFactoryResolver,
  ComponentFactory,
  Renderer2,
} from '@angular/core';
import {
  AbstractLinkFactory,
  DefaultLinkModel,
  DiagramEngine,
} from 'ngx-diagrams';
import { CustomLinkComponent } from './custom-link.component';

export class CustomLinkFactory extends AbstractLinkFactory<DefaultLinkModel> {
  constructor(
    private resolver: ComponentFactoryResolver,
    private renderer: Renderer2
  ) {
    super('custom-link');
  }

  generateWidget(
    diagramEngine: DiagramEngine,
    link: DefaultLinkModel,
    linksHost: ViewContainerRef
  ): ComponentRef<CustomLinkComponent> {
    const componentRef = linksHost.createComponent(this.getRecipe());
    link.setWidth(1);
    link.setColor('pink');
    // attach coordinates and default positional behaviour to the generated component host
    const rootNode = componentRef.location.nativeElement;

    // default style for link
    this.renderer.setStyle(rootNode, 'position', 'absolute');

    // data attributes
    this.renderer.setAttribute(rootNode, 'data-linkid', link.id);

    // on destroy make sure to destroy the componentRef
    link.onEntityDestroy().subscribe(() => {
      componentRef.destroy();
    });

    // assign all passed properties to node initialization.
    Object.entries(link).forEach(([key, value]) => {
      componentRef.instance[key] = value;
    });

    componentRef.instance.diagramEngine = diagramEngine;
    componentRef.instance.setPainted(true);
    return componentRef;
  }

  getRecipe(): ComponentFactory<CustomLinkComponent> {
    return this.resolver.resolveComponentFactory(CustomLinkComponent);
  }

  getNewInstance() {
    return new DefaultLinkModel({ type: 'custom-link' });
  }
}

import {
  ComponentFactoryResolver,
  ViewContainerRef,
  ComponentRef,
  ComponentFactory,
  Renderer2,
} from '@angular/core';
import {
  AbstractNodeFactory,
  DefaultNodeModel,
  DiagramEngine,
} from 'ngx-diagrams';
import { CustomNodeComponent } from './custom.component';

export class CustomNodeFactory extends AbstractNodeFactory<DefaultNodeModel> {
  constructor(
    private resolver: ComponentFactoryResolver,
    private renderer: Renderer2
  ) {
    super('custom-node');
  }

  generateWidget(
    diagramEngine: DiagramEngine,
    node: DefaultNodeModel,
    nodesHost: ViewContainerRef
  ): ComponentRef<CustomNodeComponent> {
    const componentRef = nodesHost.createComponent(this.getRecipe());

    // attach coordinates and default positional behaviour to the generated component host
    const rootNode = componentRef.location.nativeElement;

    // default style for node
    this.renderer.setStyle(rootNode, 'position', 'absolute');
    this.renderer.setStyle(rootNode, 'display', 'block');

    // data attributes
    this.renderer.setAttribute(rootNode, 'data-nodeid', node.id);

    // subscribe to node coordinates
    node.selectCoords().subscribe(({ x, y }) => {
      this.renderer.setStyle(rootNode, 'left', `${x}px`);
      this.renderer.setStyle(rootNode, 'top', `${y}px`);
    });

    node.selectionChanges().subscribe((e) => {
      e.isSelected
        ? this.renderer.addClass(rootNode, 'selected')
        : this.renderer.removeClass(rootNode, 'selected');
    });

    node.onEntityDestroy().subscribe((e) => {
      componentRef.destroy();
    });

    // assign all passed properties to node initialization.
    Object.entries(node).forEach(([key, value]) => {
      componentRef.instance[key] = value;
    });

    componentRef.instance.setDiagramEngine(diagramEngine);

    return componentRef;
  }

  getRecipe(): ComponentFactory<CustomNodeComponent> {
    return this.resolver.resolveComponentFactory(CustomNodeComponent);
  }

  getNewInstance(initialConfig?: any): DefaultNodeModel {
    return new DefaultNodeModel(...initialConfig);
  }
}

import {
  ViewContainerRef,
  ComponentRef,
  ComponentFactory,
  ComponentFactoryResolver,
  Renderer2,
} from '@angular/core';
import { AbstractPortFactory, DefaultPortModel } from 'ngx-diagrams';
import { Observable } from 'rxjs';
import { map, takeUntil } from 'rxjs/operators';
import { CustomPortComponent } from './custom.component';

export class CustomPortFactory extends AbstractPortFactory<
  CustomPortComponent
> {
  constructor(
    private resolver: ComponentFactoryResolver,
    private renderer: Renderer2
  ) {
    super('custom-port');
  }

  generateWidget(
    port: DefaultPortModel,
    portsHost: ViewContainerRef
  ): ComponentRef<CustomPortComponent> {
    const componentRef = portsHost.createComponent(this.getRecipe());

    // attach coordinates and default positional behaviour to the generated component host
    const rootNode = componentRef.location.nativeElement as HTMLElement;

    // data attributes
    this.renderer.setAttribute(rootNode, 'data-portid', port.id);
    this.renderer.setAttribute(rootNode, 'data-name', port.getName());

    port.in
      ? this.renderer.addClass(rootNode, 'in')
      : this.renderer.addClass(rootNode, 'out');

    // assign all passed properties to node initialization.
    Object.entries(port).forEach(([key, value]) => {
      componentRef.instance[key] = value;
    });

    // this method will add classes to all ports that have links
    this.isConnected(port).subscribe((connected) => {
      connected
        ? this.renderer.addClass(rootNode, 'connected')
        : this.renderer.removeClass(rootNode, 'connected');
    });

    port.onEntityDestroy().subscribe((e) => {
      componentRef.destroy();
    });

    return componentRef;
  }

  getRecipe(): ComponentFactory<CustomPortComponent> {
    return this.resolver.resolveComponentFactory(CustomPortComponent);
  }

  getNewInstance(initialConfig?: any): DefaultPortModel {
    return new DefaultPortModel({
      isInput: true,
      name: 'unknown',
      ...initialConfig,
    });
  }

  isConnected(port: DefaultPortModel): Observable<boolean> {
    return port.selectLinks().pipe(
      takeUntil(port.onEntityDestroy()),
      map((links) => Object.keys(links).length > 0)
    );
  }
}

