export class DefaultNodeModel extends NodeModel {
  name: string;
  color: string;
  height$: Observable<number>;
  width$: Observable<number>;

  constructor({
    name = 'Untitled',
    type = 'default',
    color = 'rgb(0, 192, 255)',
    id
  }: {
    name?: string;
    type?: string;
    id?: string;
    color?: string;
  } = {}) {
