import { PortModel, HashMap, LinkModel } from '@ngx-diagrams/core';
import { DefaultLinkModel } from './default-link.model';

export interface DefaultPortModelConfig {
  id?: string;
  isInput?: boolean;
  name?: string;
  type?: string;
  label?: string;
  linkType?: string;
  maximumLinks?: number;
}

export class DefaultPortModel extends PortModel {
  in: boolean;
  label: string;
  links: HashMap<DefaultLinkModel>;

  constructor({
    isInput = true,
    name = 'port',
    type = 'default',
    id = null,
    label = null,
    linkType = 'default',
    maximumLinks = null
  }: DefaultPortModelConfig = {}) {
    super(name, type, id, null, linkType);
    this.in = isInput;
    this.label = label || name;

    if (this.in) {
      this.setCanCreateLinks(false);
    } else {
      this.setMaximumLinks(maximumLinks);
      this.setMagnetic(false);
    }
  }

  link(port: PortModel): LinkModel {
    if (super.getCanCreateLinks()) {
      const link = new DefaultLinkModel({ type: this.getLinkType() });
      link.setSourcePort(this);
      link.setTargetPort(port);
      return link;
    }

    return null;
  }

  createLinkModel() {
    if (super.getCanCreateLinks()) {
      return new DefaultLinkModel({ type: this.getLinkType() });
    }
  }

  canLinkToPort(port: PortModel): boolean {
    if (port instanceof DefaultPortModel) {
      return this.in !== port.in;
    }
    return true;
  }
}

import { DiagramEngine } from '../services/engine.service';
import * as PF from 'pathfinding';

export const ROUTING_SCALING_FACTOR = 10;

export class PathFinding {
  private pathFinderInstance:
    | PF.JPFNeverMoveDiagonally
    | PF.JPFAlwaysMoveDiagonally
    | PF.JPFMoveDiagonallyIfNoObstacles
    | PF.JPFMoveDiagonallyIfAtMostOneObstacle;

  constructor(private diagramEngine: DiagramEngine, heuristic = PF.Heuristic.manhattan) {
    this.pathFinderInstance = new PF.AStarFinder({
      heuristic,
      diagonalMovement: PF.DiagonalMovement.Always,
      weight: 0
    });
  }

  /**
   * Taking as argument a fully unblocked walking matrix, this method
   * finds a direct path from point A to B.
   */
  calculateDirectPath(
    from: {
      x: number;
      y: number;
    },
    to: {
      x: number;
      y: number;
    }
  ): number[][] {
    const matrix = this.diagramEngine.getCanvasMatrix();
    const grid = new PF.Grid(matrix);

    const fromX = this.diagramEngine.translateRoutingX(Math.floor(from.x / ROUTING_SCALING_FACTOR));
    const toX = this.diagramEngine.translateRoutingX(Math.floor(to.x / ROUTING_SCALING_FACTOR));
    const fromY = this.diagramEngine.translateRoutingX(Math.floor(from.y / ROUTING_SCALING_FACTOR));
    const toY = this.diagramEngine.translateRoutingX(Math.floor(to.y / ROUTING_SCALING_FACTOR));

    const path = this.pathFinderInstance.findPath(fromX, fromY, toX, toY, grid);
    return path;
  }

  /**
   * Using @link{#calculateDirectPath}'s result as input, we here
   * determine the first walkable point found in the matrix that includes
   * blocked paths.
   */
  calculateLinkStartEndCoords(
    matrix: number[][],
    path: number[][]
  ): {
    start: {
      x: number;
      y: number;
    };
    end: {
      x: number;
      y: number;
    };
    pathToStart: number[][];
    pathToEnd: number[][];
  } {
    const startIndex = path.findIndex((point) => matrix[point[1]][point[0]] === 0);
    const endIndex =
      path.length -
      1 -
      path
        .slice()
        .reverse()
        .findIndex((point) => matrix[point[1]][point[0]] === 0);

    // are we trying to create a path exclusively through blocked areas?
    // if so, let's fallback to the linear routing
    if (startIndex === -1 || endIndex === -1) {
      return undefined;
    }

    const pathToStart = path.slice(0, startIndex);
    const pathToEnd = path.slice(endIndex);

    return {
      start: {
        x: path[startIndex][0],
        y: path[startIndex][1]
      },
      end: {
        x: path[endIndex][0],
        y: path[endIndex][1]
      },
      pathToStart,
      pathToEnd
    };
  }

  /**
   * Puts everything together: merges the paths from/to the centre of the ports,
   * with the path calculated around other elements.
   */
  calculateDynamicPath(
    routingMatrix: number[][],
    start: {
      x: number;
      y: number;
    },
    end: {
      x: number;
      y: number;
    },
    pathToStart: number[][],
    pathToEnd: number[][]
  ) {
    // generate the path based on the matrix with obstacles
    const grid = new PF.Grid(routingMatrix);
    const dynamicPath = this.pathFinderInstance.findPath(start.x, start.y, end.x, end.y, grid);

    // aggregate everything to have the calculated path ready for rendering
    const pathCoords = pathToStart
      .concat(dynamicPath, pathToEnd)
      .map((coords) => [
        this.diagramEngine.translateRoutingX(coords[0], true),
        this.diagramEngine.translateRoutingY(coords[1], true)
      ]);

    return PF.Util.compressPath(pathCoords);
  }
}

import { Directive, EmbeddedViewRef, Input, TemplateRef, ViewContainerRef } from '@angular/core';

class TemplateVarContext {
  $implicit: any;
  templateVar: any;
}

@Directive({
  selector: '[templateVar]'
})
export class TemplateVarDirective {
  view: EmbeddedViewRef<any>;

  protected context = new TemplateVarContext();

  @Input()
  set templateVar(context: any) {
    this.context.$implicit = this.context.templateVar = context;
    this.updateView();
  }

  constructor(protected vcRef: ViewContainerRef, protected templateRef: TemplateRef<any>) {}

  updateView() {
    if (!this.view) {
      this.vcRef.clear();
      this.view = this.vcRef.createEmbeddedView(this.templateRef, this.context);
    } else {
      this.view.markForCheck();
    }
  }
}

